mapper.select:
SELECT * FROM auth_user u JOIN auth_type t
ON u.auth_type_id = t.id
WHERE u.user_id = #{userId}
AND t.auth_type_Name = #{authTypeName}
AND u.data_id = #{dataId};

mapper.update:
UPDATE auth_user SET

    request_id = #{requestId},
    begin_date = #{beginDate},
    expire_date = #{expireDate}
    WHERE auth_type_id = (SELECT id FROM auth_type WHERE auth_type_name = #{authTypeName})
    AND user_id = #{userId}
    AND data_id = #{data}

mapper.delete:
DELETE FROM auth_user
WHERE user_id = #{userId}
AND auth_type_id = (SELECT id FROM auth_type WHERE auth_type_name = #{authTypeName})
AND data_id = #{dataId};

